import ConfirmDialog from 'themes/components/Dialogs/ConfirmDialog';

import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Form, Input, Select, DatePicker } from 'antd';
import NursingAssignService from 'themes/services/api/NursingAssignmentService';
import PatientService from 'themes/services/api/PatientService';
import UserService from 'themes/services/api/UserService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { DeleteFilled, SaveFilled, LeftCircleFilled, CopyFilled } from '@ant-design/icons';
import usePermissionHook from "themes/hooks/PermissionHook";
import moment from 'moment'
// import locale from 'antd/lib/date-picker/locale/vi_VN';

const { Option } = Select

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 4 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 18 },
  },
};

const Edit = ({ patients, users, nursingAssign }: { patients: Patient[], users : User[], nursingAssign: NursingAssignment }) => {
  const { t, notify, redirect, setStore, getAuth, getStore } = useBaseHook();
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false)
  const [form] = Form.useForm();
  const nursingAssignService = new NursingAssignService()
  const confirmRef = React.createRef<ConfirmDialog>()

  const { checkPermission } = usePermissionHook();
  const deletePer = checkPermission({
    "admin.nursingAssignments": "D"
  })
  //submit form
  const onFinish = async (values: any): Promise<void> => {
    setLoading(true)
    let [error, result]: any[] = await to(nursingAssignService.edit({
      id: nursingAssign.id,
      values
    }));
    setLoading(false)

    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }
    notify(t("message.recordUpdated"))
    redirect("frontend.admin.nursingAssignments.index")
  }

  const onDelete = async (): Promise<void> => {
    let [error, result]: any[] = await to(nursingAssignService.delete({ ids: [nursingAssign.id] }));

    if (error) {
      return notify(t(`errors:${error.code}`), t(`${error.message}`), 'error')
    }
    notify(t('message.recordDeleted'))
    redirect("frontend.admin.nursingAssignments.index")
  }

  //render option
  const renderOptionPatient = (): JSX.Element[] => {
    let result: JSX.Element[] = [];
    patients.map(item => {
      result.push(
      <Option value={item.id} key={item.id}>{item.fullname} - {item.roomCode} - {item.bedCode}</Option>
      )
    })
    return result;
  }

  const renderOptionUser = (): JSX.Element[] => {
    let result: JSX.Element[] = [];
    users.map(item => {
      item.position = item.position? item.position.replace(/^[\s\uFEFF\xA0]+|[\s\uFEFF\xA0]+$/g, '') : ""
      if(item.position == "Bác sỹ")
      result.push(
      <Option value={item.id} key={item.id}>{item.code} - {item.fullname}</Option>
      )
    })
    return result;
  }

  const tzDate = (value: any) => {
    moment.parseZone(value.format("YYYY-MM-DD HH:mm:ss"), "Asia/Ho_Chi_Minh")
  }

  const renderDeleteDialog = () => {
    return (
      <ConfirmDialog
        ref={confirmRef}
        onSubmit={onDelete}
        title={t('deleteItem')}
        content={t('message.deleteConfirm')}
      />
    )
  }

  return <Layout>
    <div className="content">
      <Form
        {...formItemLayout}
        form={form}
        name="createPatient"
        initialValues={{
          nurseId: nursingAssign.nurseId,
          patientId: nursingAssign.patientId,
          dateBegin: nursingAssign.dateBegin ? moment(nursingAssign.dateBegin) : "",
          dateEnd: nursingAssign.dateEnd ? moment(nursingAssign.dateEnd) : "",
          note: nursingAssign.note
        }}
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item
          label={t("nursingAssignments.nurseCode")}
          name="nurseId"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Select>
            {renderOptionUser()}
          </Select>
        </Form.Item>

        <Form.Item
          label={t("nursingAssignments.patientCode")}
          name="patientId"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Select>
            {renderOptionPatient()}
          </Select>
        </Form.Item>

        <Form.Item
          label={t("nursingAssignments.dateBegin")}
          name="dateBegin"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <DatePicker format={'YYYY-MM-DD'}  />
        </Form.Item>

        <Form.Item
          label={t("nursingAssignments.dateEnd")}
          name="dateEnd"
        >
          <DatePicker format={'YYYY-MM-DD'} onChange={() => {}}/>
        </Form.Item>

        <Form.Item
          label={t("note")}
          name="note"
        >
          <Input />
        </Form.Item>

        <Form.Item wrapperCol={{ span: 24 }} style={{ textAlign: "center" }}>
          <Button onClick={() => redirect("frontend.admin.nursingAssignments.index")} style={{ marginRight: 8 }}>
            <LeftCircleFilled /> {t('cancel')}
          </Button>

          <Button type="primary" htmlType="submit" loading={loading} style={{ marginRight: 8 }}>
            <SaveFilled /> {t('submit')}
          </Button>
          
          <Button hidden={!deletePer} danger onClick={() => { 
              if (confirmRef.current) confirmRef.current.show(nursingAssign.id) 
            }}>
            <DeleteFilled /> {t('deleteItem')}
          </Button>
         
        </Form.Item>
      </Form>
      {renderDeleteDialog()}
    </div>
  </Layout>
}

Edit.getInitialProps = async (ctx: any) => {
  const nursingAssignService = new NursingAssignService(ctx)
  const patientService = new PatientService(ctx)
  const userService = new UserService(ctx)
  const query = ctx.query;
  let error: any;

  if (!query.id) {
    //error missing id
    error = {
      code: 9996,
      message: 'missing ID'
    }
  }

  let [nursingAssignError, nursingAssign] = await to(nursingAssignService.detail({ id: query.id }));
  const [errorPatient, patients] = await to(patientService.index({ pageSize: -1 }));
  const [errorUser, users] = await to(userService.index({ pageSize: -1 }));
  return {
    namespacesRequired: ['common', 'pages', 'menu'],
    error: error,
    nursingAssign: nursingAssign,
    patients: _.get(patients, "data", []),
    users: _.get(users, "data", [])
  }
}
Edit.permissions = {
  "admin.doctorAssignments": "U"
};
export default Edit
