import ConfirmDialog from 'themes/components/Dialogs/ConfirmDialog';

import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Row, Col, Form, Input, Select, Typography } from 'antd';
import RoomService from 'themes/services/api/RoomService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { LeftCircleFilled, SaveFilled, DeleteFilled } from '@ant-design/icons';
import BreadCrumb from 'themes/components/Breadcumb'
import usePermissionHook from "themes/hooks/PermissionHook";
const { Title, Text } = Typography;

const { Option } = Select

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 4 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 18 },
  },
};


const Edit = ({ room }: { room: Room }) => {
  const { t, notify, redirect, router } = useBaseHook();
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false)
  const [form] = Form.useForm();
  const roomService = new RoomService()
  const confirmRef = React.createRef<ConfirmDialog>()
  const { checkPermission } = usePermissionHook();
  const deletePer = checkPermission({
    "admin.rooms": "D"
  })
  //submit form
  const onFinish = async (values: any): Promise<void> => {
    setLoading(true)
    let [error, result]: any[] = await to(roomService.edit({
      id: room.id,
      values
    }));
    setLoading(false)

    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }
    notify(t("message.recordUpdated"))
    redirect("frontend.admin.rooms.index")
  }

  const onDelete = async (): Promise<void> => {
    let [error, result]: any[] = await to(roomService.delete({ ids: [room.id] }));

    if (error) {
      return notify(t(`errors:${error.code}`), t(`${error.message}`), 'error')
    }
    notify(t('message.recordDeleted'))
    redirect("frontend.admin.rooms.index")
  }

  const renderDeleteDialog = () => {
    return (
      <ConfirmDialog
        ref={confirmRef}
        onSubmit={onDelete}
        title={t('deleteItem')}
        content={t('message.deleteConfirm')}
      />
    )
  }


  return <Layout>
    <div className="content">
      <Form
        {...formItemLayout}
        form={form}
        name="createRoom"
        initialValues={{
          code: room.code,
          bedNumber: room.bedNumber,
          note: room.note,
        }}
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item
          label={t("code")}
          name="code"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("bedNumber")}
          name="bedNumber"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <InputNumber style={{ width: "100%" }} />
        </Form.Item>
        <Form.Item
          label={t("note")}
          name="note"
        >
          <Input />
        </Form.Item>
        <Form.Item wrapperCol={{ span: 24 }} style={{ textAlign: "center" }}>
          <Button onClick={() => router.back()} style={{ marginRight: 8 }}>
            <LeftCircleFilled /> {t('cancel')}
          </Button>
          <Button type="primary" htmlType="submit" loading={loading} style={{ marginRight: 8 }}>
            <SaveFilled /> {t('submit')}
          </Button>
          <Button hidden={!deletePer} danger onClick={() => { if (confirmRef.current) confirmRef.current.show(room.id) }}>
            <DeleteFilled /> {t('deleteItem')}
          </Button>
        </Form.Item>
      </Form>
      {renderDeleteDialog()}
    </div>
  </Layout>
}

Edit.getInitialProps = async (ctx: any) => {
  const roomService = new RoomService(ctx)
  const query = ctx.query;
  let error: any;

  if (!query.id) {
    //error missing id
    error = {
      code: 9996,
      message: 'missing ID'
    }
  }

  let [roomError, group] = await to(roomService.detail({ id: query.id }));
  return {
    namespacesRequired: ['common', 'pages', 'menu'],
    error: error,
    group: group,
  }
}
Edit.permissions = {
  "admin.rooms": "U"
}
export default Edit
