import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Row, Col, Form, Input, Select, Typography, Checkbox } from 'antd';
import { Table } from 'antd';
import { AppstoreOutlined, MailOutlined, SettingOutlined } from '@ant-design/icons';
import UserPermissionService from 'themes/services/api/UserPermissionService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { LeftCircleFilled, SaveFilled, DeleteFilled } from '@ant-design/icons';
import BreadCrumb from 'themes/components/Breadcumb'
import { getInputClassName } from 'antd/lib/input/Input';
const { Title, Text } = Typography;

const Index = ({ permissions, total }: { permissions: Permission[], total: number }) => {
  let result: any = {}
  const { t, notify, redirect, router } = useBaseHook();
  const PermissionService = new UserPermissionService()

  const renderCheckbox = (row: any, permission: number) => {
    // const checked = (row.value & permission) === permission
    const checked = (row.avalibleValue & permission) !== permission
    return <Checkbox defaultChecked={checked} disabled={true} ></Checkbox>
  }

    const columns = [
      {
        title: t('userPermissions.key'),
        dataIndex: 'keyName',
        key: 'key',
        width: '5%',
      },
      {
        title: t('userPermissions.name'),
        dataIndex: 'name',
        key: 'name',
      },
      {
        title: t('description'),
        dataIndex: 'description',
        key: 'description',
      },
      {
        title: t("create"),
        dataIndex: 'value',
        key: 'valueC',
        width: '10%',
        render: (value: any, row: any) => renderCheckbox(row, 8)
      },
      {
        title: t("view"),
        dataIndex: 'value',
        key: 'valueR',
        width: '10%',
        render: (value: any, row: any) => renderCheckbox(row, 4)
      },
      {
        title: t("edit"),
        dataIndex: 'value',
        key: 'valueU',
        width: '10%',
        render: (value: any, row: any) => renderCheckbox(row, 2)
      },
      {
        title: t("delete"),
        dataIndex: 'value',
        key: 'valueD',
        width: '10%',
        render: (value: any, row: any) => renderCheckbox(row, 1)
      }
    ];
    
  return <Layout>
    <div className="content">
      <Table size="small" columns={columns} dataSource={permissions} />
    </div>
  </Layout>
}

Index.getInitialProps = async (ctx: any) => {
  let permissionService = new UserPermissionService(ctx)
  let [error, permissions]: any[] = await to(permissionService.index())
  return {
    namespacesRequired: ['common', 'pages', 'menu','errors'],
    error: error,
    permissions: useBaseHook.getData(permissions, "data", []),
    total: useBaseHook.getData(permissions, "total", 0)
  }
}
Index.permissions = {
  "admin.userPermissions": "R"
};
export default Index
