import Layout from 'themes/layouts/Admin'
import { GridTable, GridTableHelper, FilterInput } from 'themes/components/Table';
import ConfirmDialog from 'themes/components/Dialogs/ConfirmDialog';
import { Button } from 'antd';
import NursingAssignService from 'themes/services/api/NursingAssignmentService';
import _ from 'lodash'
import to from 'await-to-js'
import React, { useState } from 'react';
import useBaseHook from 'themes/hooks/BaseHooks'
import usePermissionHook from "themes/hooks/PermissionHook";
import {PlusCircleOutlined, DeleteOutlined} from '@ant-design/icons';

const formaterDate = (value: any) => {
  value = value? String(value) : ""
  value = value.substr(0,10)
  return `${value}`
}

const Index = ({ nursingAssigns, total }: { nursingAssigns: NursingAssignment[], total: number }) => {
  const { t, notify, redirect, setStore, getAuth, getStore } = useBaseHook();
  const [hiddenDeleteBtn, setHiddenDeleteBtn] = useState(true)
  const [selectedIds, setSelectedIds] = useState([])
  const nursingAssignService = new NursingAssignService()
  const confirmRef = React.createRef<ConfirmDialog>()
  const tableRef = React.createRef<GridTable>()
  const { checkPermission } = usePermissionHook();
  const createPer = checkPermission({
    "admin.nursingAssignments": "C"
  })
  const deletePer = checkPermission({
    "admin.nursingAssignment": "D"
  })
  const columns = [
    {
      title: t("nurse"),
      dataIndex: 'nurseName',
      key: 'users.fullname',
      sorter:true,
      filterable: true,
      render: (text: string, record: any) =>
        <a onClick={() => redirect('frontend.admin.nursingAssignments.edit', 
        { id: record.id })}>{record.code} - {record.nurseName}</a>
    },
    {
      title: t('patient'),
      dataIndex: 'patientName',
      key: 'patients.fullname',
      sorter:true,
      filterable: true,
      render: (text: string, record: any) => <div>{record.patientName} - {record.roomCode} - {record.bedCode}</div>
    },
    {
      title: t("nursingAssignments.dateBegin"),
      dataIndex: 'dateBegin',
      key: 'dateBegin',
      sorter:true,
      filterDate: true,
      width: '15%',
      render: (text: string, record: any) => <div>{formaterDate(text)}</div>
    },
    {
      title: t("nursingAssignments.dateEnd"),
      dataIndex: 'dateEnd',
      key: 'dateEnd',
      sorter:true,
      filterDate: true,
      width: '15%',
      render: (text: string, record: any) => <div>{formaterDate(text)}</div>
    },
    {
      title: t("note"),
      dataIndex: 'note',
      key: 'note',
    },
  ]

  const onChangeSelection = (data: any) => {
    if (data.length > 0) {
      setHiddenDeleteBtn(false)
    }
    else {
      setHiddenDeleteBtn(true)
    }
    setSelectedIds(data)
  }

  const fetchData = async (values: any) => {
    let [error, nursingAssigns]: any[] = await to(nursingAssignService.index(values))
    if (error) {
      const { code, message } = error
      notify(t(`errors:${code}`), t(message), 'error')
      return {}
    }
    return nursingAssigns
  }

  const onDelete = async () => {
    let [error, result]: any[] = await to(nursingAssignService.delete({ ids: selectedIds }));
    if (error) {
      return notify(t(`errors:${error.code}`), t(`${error.message}`), 'error')
    }
    notify(t("message.recordDeleted"));
    if (tableRef.current !== null) {
      tableRef.current.reload()
    }
    setSelectedIds([])
    setHiddenDeleteBtn(true)
  }

  const renderDeleteDialog = () => {
    return (
      <ConfirmDialog
        ref={confirmRef}
        onSubmit={onDelete}
        title={t('deleteItem')}
        content={t('message.deleteConfirm')}
      />
    )
  }

  return <Layout>
    <div className="content">
      <Button hidden={!createPer} onClick={() => redirect("frontend.admin.nursingAssignments.create")} type="primary" className='btn-top'>
        <PlusCircleOutlined />
        {t('create')}
      </Button>

      <Button danger onClick={() => { if (confirmRef.current) confirmRef.current.show() }} className='btn-top' hidden={hiddenDeleteBtn || !deletePer}>
        <DeleteOutlined />
        {t('delete')}
      </Button>
      
      <GridTable
        ref={tableRef}
        columns={columns}
        data={nursingAssigns}
        rowSelection={{selectedRowKeys: selectedIds, onChange: (data: any[]) => onChangeSelection(data) }}
        fetchData={fetchData}
        total={total}
      />
      {renderDeleteDialog()}
    </div>
  </Layout>
}

Index.getInitialProps = async (ctx: any) => {
  let nursingAssignService = new NursingAssignService(ctx)
  let defaultOptions = {
    filters: [],
    sorting: []
  }
  let query = ctx.query
  let filters = GridTableHelper.getOptions(query, defaultOptions);
  let [error, nursingAssigns]: any[] = await to(nursingAssignService.index(filters))
  return {
    namespacesRequired: ['common', 'pages', 'menu', 'errors'],
    error: error,
    nursingAssigns: useBaseHook.getData(nursingAssigns, "data", []),
    total: useBaseHook.getData(nursingAssigns, "total", 0),
    defaultQuery: {
      filters: GridTableHelper.makeQuery(filters)
    }
  }
}
Index.permissions = {
  "admin.nursingAssignments": "R",
  "nurse.nursingAssignments": "R",
  "admin.users.nursingAssignments": "R"
};
export default Index
