import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Row, Col, Form, Input, Select,  DatePicker, Radio } from 'antd';
import UserService from 'themes/services/api/UserService';
import UserGroupService from 'themes/services/api/UserGroupService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { DeleteFilled, LeftCircleFilled } from '@ant-design/icons';
import moment from 'moment';

const { Option } = Select

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 4 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 18 },
  },
};

const Create = ({ groups }: { groups: UserGroup[] }) => {
  const { t, notify, redirect, setStore, getAuth, getStore } = useBaseHook();
  const [loading, setLoading] = useState(false);
  const [form] = Form.useForm();
  const userService = new UserService()
  //submit form
  const onFinish = async (values: any): Promise<void> => {
    setLoading(true)
    let { rePassword, ...otherValues } = values;
    let [error, result]: any[] = await to(userService.create(otherValues));
    setLoading(false)

    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }
    notify(t("message.recordCreated"))
    redirect("frontend.admin.users.index")
  }

  //validate input password
  const validatorPassword = ({ getFieldValue }: { getFieldValue: Function }) => ({
    validator: (rule: any, value: any) => {
      if (!value || getFieldValue('password') === value) {
        return Promise.resolve();
      }
      return Promise.reject(t('form.rePassword'));
    }
  })

  //render option
  const renderOption = (): JSX.Element[] => {
    let result: JSX.Element[] = [];
    groups.map(item => {
      result.push(
        <Option value={item.id} key={item.id}>{item.name}</Option>
      )
    })
    return result;
  }
  
  return <Layout>
    <div className="content">
      <Form
        {...formItemLayout}
        form={form}
        name="createUser"
        initialValues={{
          code: "",
          fullname: "",
          position: "",
          birdthday: "",
          gender: "",
          address: "",
          cardId: "",
          email: "",
          phone: "",
          username: "",
          password: "",
          rePassword: "",
          groupId: ""
        }}
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item
          label={t("code")}
          name="code"
          rules={[
            { required: true, message: t('form.required'), whitespace: true },
            { max: 10, message: t('form.maxLength', { length: 10 }) }
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("fullname")}
          name="fullname"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>
        
        <Form.Item
          label={t("position")}
          name="position"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("birthday")}
          name="birthday"
        >
          <DatePicker format={'YYYY-MM-DD'} showTime={false}/>
        </Form.Item>

        <Form.Item
          label={t("gender")}
          name="gender"
        >
          <Radio.Group>
            <Radio value={0} key={0}>{t("male")}</Radio>
            <Radio value={1} key={1}>{t("female")}</Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item
          label={t("address")}
          name="address"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("cardId")}
          name="cardId"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("email")}
          name="email"
          rules={[
            { type: 'email', message: t('form.email') }
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("phone")}
          name="phone"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("username")}
          name="username"
          rules={[
            { required: true, message: t('form.required'), whitespace: true },
            { max: 15, message: t('form.maxLength', { length: 15 }) }
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("password")}
          name="password"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input.Password />
        </Form.Item>

        <Form.Item
          label={t("rePassword")}
          name="rePassword"
          rules={[
            { required: true, message: t('form.required') },
            validatorPassword
          ]}
        >
          <Input.Password />
        </Form.Item>

        <Form.Item
          label={t("userGroup")}
          name="groupId"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Select>
            {renderOption()}
          </Select>
        </Form.Item>

        <Form.Item wrapperCol={{ span: 24 }} style={{textAlign: "center"}}>
          <Button onClick={() => redirect("frontend.admin.users.index")} style={{ marginRight: 8}}>
            <LeftCircleFilled /> {t('cancel')}
          </Button>
          <Button type="primary" htmlType="submit" loading={loading}>
            <DeleteFilled /> {t('submit')}
          </Button>
        </Form.Item>
      </Form>
    </div>
  </Layout>
}

Create.getInitialProps = async (ctx: any) => {
  const userGroupService = new UserGroupService(ctx)
  const [error, groups] = await to(userGroupService.index({ pageSize: -1 }));
  return {
    namespacesRequired: ['common', 'pages','menu'],
    error: error,
    groups: _.get(groups, "data", []),
  }
}
Create.permissions = {
  "admin.users": "C"
};
export default Create
