import ConfirmDialog from 'themes/components/Dialogs/ConfirmDialog';

import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Row, Col, Form, Input, Select, Modal, InputNumber, Radio, DatePicker } from 'antd';
import PatientService from 'themes/services/api/PatientService';
import BedService from 'themes/services/api/BedService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { DeleteFilled, SaveFilled, LeftCircleFilled, CopyFilled } from '@ant-design/icons';
import usePermissionHook from "themes/hooks/PermissionHook";
import moment from 'moment'
import locale from 'antd/lib/date-picker/locale/vi_VN';

const { Option } = Select

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 4 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 18 },
  },
};

const Edit = ({ beds, patient }: { beds: Bed[], patient: Patient }) => {
  const { t, notify, redirect, setStore, getAuth, getStore } = useBaseHook();
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false)
  const [form] = Form.useForm();
  const patientService = new PatientService()
  const confirmRef = React.createRef<ConfirmDialog>()

  const { checkPermission } = usePermissionHook();
  const deletePer = checkPermission({
    "admin.patients": "D"
  })
  //submit form
  const onFinish = async (values: any): Promise<void> => {
    setLoading(true)
    let [error, result]: any[] = await to(patientService.edit({
      id: patient.id,
      values
    }));
    setLoading(false)

    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }
    notify(t("message.recordUpdated"))
    redirect("frontend.admin.patients.index")
  }

  const onDelete = async (): Promise<void> => {
    let [error, result]: any[] = await to(patientService.delete({ ids: [patient.id] }));

    if (error) {
      return notify(t(`errors:${error.code}`), t(`${error.message}`), 'error')
    }
    notify(t('message.recordDeleted'))
    redirect("frontend.admin.patients.index")
  }

  //render option
  const renderOption = (): JSX.Element[] => {
    let result: JSX.Element[] = [];
    beds.map(item => {
      result.push(
        <Option value={item.id} key={item.id}>{item.roomCode} - {item.bedId}</Option>
      )
    })
    return result;
  }
  const renderDeleteDialog = () => {
    return (
      <ConfirmDialog
        ref={confirmRef}
        onSubmit={onDelete}
        title={t('deleteItem')}
        content={t('message.deleteConfirm')}
      />
    )
  }

  return <Layout>
    <div className="content">
      <Form
        {...formItemLayout}
        form={form}
        name="createPatient"
        initialValues={{
          fullname: patient.fullname,
          birthday: patient.birthday ? moment(patient.birthday).zone(-1) : "",
          gender: patient.gender,
          address: patient.address,
          cardId: patient.cardId,
          phone: patient.phone,
          medicalHistory: patient.medicalHistory,
          dateIn: patient.dateIn ? moment(patient.dateIn).zone(-1) : "",
          dateOut: patient.dateOut ? moment(patient.dateOut).zone(-1) : "",
          bedId: patient.bedId
        }}
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item
          label={t("fullname")}
          name="fullname"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("birthday")}
          name="birthday"
        >
          <DatePicker format={'YYYY-MM-DD'} showTime={false}/>
        </Form.Item>

        <Form.Item
          label={t("gender")}
          name="gender"
        >
          <Radio.Group>
            <Radio value={0} key={0}>{t("male")}</Radio>
            <Radio value={1} key={1}>{t("female")}</Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item
          label={t("address")}
          name="address"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("cardId")}
          name="cardId"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("phone")}
          name="phone"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("patients.medicalHistory")}
          name="medicalHistory"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("patients.dateIn")}
          name="dateIn"
        >
          <DatePicker format={'YYYY-MM-DD'} showTime={false} />
        </Form.Item>

        <Form.Item
          label={t("patients.dateOut")}
          name="dateOut"
        >
          <DatePicker format={'YYYY-MM-DD'} showTime={false}/>
        </Form.Item>

        <Form.Item
          label={t("patients.bedCode")}
          name="bedId"
        >
          <Select>
            {renderOption()}
          </Select>
        </Form.Item>

        <Form.Item wrapperCol={{ span: 24 }} style={{ textAlign: "center" }}>
          <Button onClick={() => redirect("frontend.admin.patients.index")} style={{ marginRight: 8 }}>
            <LeftCircleFilled /> {t('cancel')}
          </Button>

          <Button type="primary" htmlType="submit" loading={loading} style={{ marginRight: 8 }}>
            <SaveFilled /> {t('submit')}
          </Button>
          
          <Button hidden={!deletePer} danger onClick={() => { 
              if (confirmRef.current) confirmRef.current.show(patient.id) 
            }}>
            <DeleteFilled /> {t('deleteItem')}
          </Button>
         
        </Form.Item>
      </Form>
      {renderDeleteDialog()}
    </div>
  </Layout>
}

Edit.getInitialProps = async (ctx: any) => {
  const patientService = new PatientService(ctx)
  const bedService = new BedService(ctx)
  const query = ctx.query;
  let error: any;

  if (!query.id) {
    //error missing id
    error = {
      code: 9996,
      message: 'missing ID'
    }
  }

  let [patientError, patient] = await to(patientService.detail({ id: query.id }));
  const [errorBed, beds] = await to(bedService.index({ pageSize: -1 }));
  return {
    namespacesRequired: ['common', 'pages', 'menu'],
    error: error,
    patient: patient,
    beds: _.get(beds, "data", []),
  }
}
Edit.permissions = {
  "admin.patients": "U"
};
export default Edit
