import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Row, Col, Form, Input, Select, Typography, Checkbox } from 'antd';
import UserPermissionService from 'themes/services/api/UserPermissionService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { LeftCircleFilled, SaveFilled } from '@ant-design/icons';
import BreadCrumb from 'themes/components/Breadcumb'
const { Title, Text } = Typography;

const { Option } = Select

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 4 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 18 },
  },
};


const Create = () => {
  const { t, notify, redirect, router } = useBaseHook();
  const [loading, setLoading] = useState(false);
  const [form] = Form.useForm();
  const permissionService = new UserPermissionService()
  let perValue = 0
  //submit form
  const onFinish = async (values: any): Promise<void> => {
    setLoading(true)
    let [error, result]: any[] = await to(permissionService.create(values));
    setLoading(false)
    console.log(values)

    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }
    notify(t("message.recordCreated"))
    redirect("frontend.admin.userPermissions.index")
  }

  const options = [
    { label: t("create"), value: 8 },
    { label: t("view"), value: 4 },
    { label: t("edit"), value: 2 },
    { label: t("delete"), value: 1 }
  ];

  const onChange = (e:any, avalibleValue:Number = 0) => {
    avalibleValue += e
    console.log(avalibleValue)
  }

  return <Layout>
    <div className="content">
      <Form
        {...formItemLayout}
        form={form}
        name="createPermission"
        initialValues={{
          key: "",
          name: "",
          description: "",
          avalibleValue: 0
        }}
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item
          label={t("userPermissions.key")}
          name="key"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("userPermissions.name")}
          name="name"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("description")}
          name="description"
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("userPermissions.avalibleValue")}
          name="avalibleValue"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Checkbox.Group options={options} onChange={onChange} />
        </Form.Item>

        <Form.Item wrapperCol={{ span: 24 }} style={{ textAlign: "center" }}>
          <Button onClick={() => router.back()} style={{ marginRight: 8 }}>
            <LeftCircleFilled /> {t('cancel')}
          </Button>
          <Button type="primary" htmlType="submit" loading={loading} style={{ marginRight: 8 }}>
            <SaveFilled /> {t('submit')}
          </Button>
        </Form.Item>
      </Form>
    </div>
  </Layout>
}

Create.getInitialProps = async (ctx: any) => {
  const permissionService = new UserPermissionService(ctx)
  const [error, permissions] = await to(permissionService.index({ pageSize: -1 }));
  return {
    namespacesRequired: ['common', 'pages', 'menu','errors'],
    error: error,
    groups: _.get(permissions, "data", []),
  }
}
Create.permissions = {
  "admin.userPermissions": "C"
}
export default Create
