import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Row, Col, Form, Input, Select, InputNumber, Radio, DatePicker } from 'antd';
import PatientService from 'themes/services/api/PatientService';
import BedService from 'themes/services/api/BedService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { DeleteFilled, LeftCircleFilled } from '@ant-design/icons';
import locale from 'antd/lib/date-picker/locale/vi_VN';

const { Option } = Select

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 4 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 18 },
  },
};

const Create = ({ beds }: { beds: Bed[] }) => {
  const { t, notify, redirect, setStore, getAuth, getStore } = useBaseHook();
  const [loading, setLoading] = useState(false);
  const [form] = Form.useForm();
  const patientService = new PatientService()
  //submit form
  const onFinish = async (values: any): Promise<void> => {
    setLoading(true)
    let [error, result]: any[] = await to(patientService.create(values));
    setLoading(false)

    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }
    notify(t("message.recordCreated"))
    redirect("frontend.admin.patients.index")
  }

  //render option
  const renderOption = (): JSX.Element[] => {
    let result: JSX.Element[] = [];
    beds.map(item => {
      result.push(
        <Option value={item.id} key={item.id}>{item.roomCode} - {item.bedId}</Option>
      )
    })
    return result;
  }
  
  return <Layout>
    <div className="content">
      <Form
        {...formItemLayout}
        form={form}
        name="createPatient"
        initialValues={{
          fullname: "",
          birthday: "",
          gender: "",
          address: "",
          cardId: "",
          phone: "",
          medicalHistory: "",
          dateIn: "",
          dateOut: "",
          bedId: ""
        }}
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item
          label={t("fullname")}
          name="fullname"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("birthday")}
          name="birthday"
        >
          <DatePicker format={'YYYY-MM-DD'} showTime={false} locale={locale}/>
        </Form.Item>

        <Form.Item
          label={t("gender")}
          name="gender"
        >
          <Radio.Group>
            <Radio value={0} key={0}>{t("male")}</Radio>
            <Radio value={1} key={1}>{t("female")}</Radio>
          </Radio.Group>
        </Form.Item>

        <Form.Item
          label={t("address")}
          name="address"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("cardId")}
          name="cardId"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("phone")}
          name="phone"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("patients.medicalHistory")}
          name="medicalHistory"
        >
          <Input />
        </Form.Item>

        <Form.Item
          label={t("patients.dateIn")}
          name="dateIn"
        >
          <DatePicker format={'YYYY-MM-DD'} showTime={false}/>
        </Form.Item>

        <Form.Item
          label={t("patients.dateOut")}
          name="dateOut"
        >
          <DatePicker format={'YYYY-MM-DD'} showTime={false}/>
        </Form.Item>

        <Form.Item
          label={t("patients.bedCode")}
          name="bedId"
        >
          <Select>
            {renderOption()}
          </Select>
        </Form.Item>

        <Form.Item wrapperCol={{ span: 24 }} style={{textAlign: "center"}}>
          <Button onClick={() => redirect("frontend.admin.patients.index")} style={{ marginRight: 8}}>
            <LeftCircleFilled /> {t('cancel')}
          </Button>
          <Button type="primary" htmlType="submit" loading={loading}>
            <DeleteFilled /> {t('submit')}
          </Button>
        </Form.Item>
      </Form>
    </div>
  </Layout>
}

Create.getInitialProps = async (ctx: any) => {
  const bedService = new BedService(ctx)
  const [error, beds] = await to(bedService.index({ pageSize: -1 }));
  return {
    namespacesRequired: ['common', 'pages','menu'],
    error: error,
    beds: _.get(beds, "data", []),
  }
}
Create.permissions = {
  "admin.patients": "C"
};
export default Create
