import ConfirmDialog from 'themes/components/Dialogs/ConfirmDialog';

import React, { useState } from 'react'
import Layout from 'themes/layouts/Admin'
import { Button, Row, Col, Form, Input, Select, Modal, InputNumber } from 'antd';
import UserService from 'themes/services/api/UserService';
import UserGroupService from 'themes/services/api/UserGroupService';
import _ from 'lodash';
import to from 'await-to-js'
import useBaseHook from 'themes/hooks/BaseHooks'
import { DeleteFilled, SaveFilled, LeftCircleFilled, CopyFilled } from '@ant-design/icons';
import usePermissionHook from "themes/hooks/PermissionHook";

const { Option } = Select

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 4 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 18 },
  },
};

const formatCurrency = (value: any) =>`${value}`.replace(/[^0-9]+/g, "").replace(/\B(?=(\d{3})+(?!\d))/g, ",")
const parserCurrency = (value: any) =>value ? value.replace(/\$\s?|(,*)/g, "") : ""

const Edit = ({ groups, user }: { groups: UserGroup[], user: User }) => {
  const { t, notify, redirect, setStore, getAuth, getStore } = useBaseHook();
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false)
  const [form] = Form.useForm();
  const userService = new UserService()
  const confirmRef = React.createRef<ConfirmDialog>()

  const { checkPermission } = usePermissionHook();
  const deletePer = checkPermission({
    "admin.users": "D"
  })
  //submit form
  const onFinish = async (values: any): Promise<void> => {
    setLoading(true)
    let { rePassword, ...otherValues } = values;
    let [error, result]: any[] = await to(userService.edit({
      id: user.id,
      ...otherValues
    }));
    setLoading(false)

    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }
    notify(t("message.recordUpdated"))
    redirect("frontend.admin.users.index")
  }

  const onDelete = async (): Promise<void> => {
    let [error, result]: any[] = await to(userService.delete({ ids: [user.id] }));

    if (error) {
      return notify(t(`errors:${error.code}`), t(`${error.message}`), 'error')
    }
    notify(t('message.recordDeleted'))
    redirect("frontend.admin.users.index")
  }
  
  const onChangePassword = async (data: any): Promise<void> => {
    console.log(data)
    setModalVisible(false)
    let values = { id: user.id, password: data.password }

    let [error, result]: any[] = await to(userService.updatePassword(values));
    if (error) {
      return notify(t(`errors:${error.code}`), t(error.message), 'error')
    }

    notify(t("message.recordUpdated"))
  }

  //validate input password
  const validatorPassword = ({ getFieldValue }: { getFieldValue: Function }) => ({
    validator: (rule: any, value: any) => {
      if (!value || getFieldValue('password') === value) {
        return Promise.resolve();
      }
      return Promise.reject(t('form.rePassword'));
    }
  })

  //render option
  const renderOption = (): JSX.Element[] => {
    let result: JSX.Element[] = [];
    groups.map(item => {
      result.push(
        <Option value={item.id} key={item.id}>{item.name}</Option>
      )
    })
    return result;
  }
  const renderDeleteDialog = () => {
    return (
      <ConfirmDialog
        ref={confirmRef}
        onSubmit={onDelete}
        title={t('deleteItem')}
        content={t('message.deleteConfirm')}
      />
    )
  }

  const renderChangePassModal = () => {
    const [formChangePassword] = Form.useForm()
    return (<Form form={formChangePassword} name="formChangePassword"
      initialValues={{
        password: "",
        repassword: "",
      }}
      onFinish={onChangePassword}
    >
      <Modal
        closable={false}
        visible={modalVisible}
        onCancel={() => setModalVisible(false)}
        onOk={() => { formChangePassword.submit(); }}
      >
        <Form.Item
          label={t("password")}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 19 }}
          name="password"
          rules={[
            { required: true, message: t('form.required') }
          ]}
        >
          <Input.Password
            placeholder={t("password")}
            type="password"
            autoFocus={true}
          />
        </Form.Item>
        <Form.Item
          label={t("rePassword")}
          labelCol={{ span: 5 }}
          wrapperCol={{ span: 19 }}
          name="repassword"
          rules={[
            { required: true, message: t('form.required') },
            validatorPassword
          ]}
        >
          <Input.Password
            placeholder={t("rePassword")}
          />
        </Form.Item>
      </Modal>
    </Form>
    )
  }

  return <Layout>
    <div className="content">
      <Form
        {...formItemLayout}
        form={form}
        name="createUser"
        initialValues={{
          fullname: user.fullname,
          username: user.username,
          email: user.email,
          phone: user.phone,
          groupId: user.groupId
        }}
        onFinish={onFinish}
        scrollToFirstError
      >
        <Form.Item
          label={t("fullname")}
          name="fullname"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("username")}
          name="username"
          rules={[
            { required: true, message: t('form.required'), whitespace: true },
            { max: 15, message: t('form.maxLength', { length: 15 }) }
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("email")}
          name="email"
          rules={[
            { required: true, message: t('form.required') },
            { type: 'email', message: t('form.email') }
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("phone")}
          name="phone"
          rules={[
            { required: true, message: t('form.required') },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label={t("userGroup")}
          name="groupId"
          rules={[
          ]}
        >
          <Select>
            {renderOption()}
          </Select>
        </Form.Item>
        <Form.Item
            label={t("salary.salary")}
            name="salary"
          >
            <InputNumber
              style={{ width: "100%" }}
              formatter={formatCurrency}
              parser={parserCurrency}
            />
        </Form.Item>
        <Form.Item wrapperCol={{ span: 24 }} style={{ textAlign: "center" }}>
          <Button onClick={() => redirect("frontend.admin.users.index")} style={{ marginRight: 8 }}>
            <LeftCircleFilled /> {t('cancel')}
          </Button>

          <Button type="primary" htmlType="submit" loading={loading} style={{ marginRight: 8 }}>
            <SaveFilled /> {t('submit')}
          </Button>
          <Button onClick={() => { setModalVisible(true) }} style={{ marginRight: 8 }}>
            <CopyFilled /> {t('changePassword')}
          </Button>
          
          <Button hidden={!deletePer} danger onClick={() => { if (confirmRef.current) confirmRef.current.show(user.id) }}>
            <DeleteFilled /> {t('deleteItem')}
          </Button>
         
        </Form.Item>
      </Form>
      {renderDeleteDialog()}
      {renderChangePassModal()}
    </div>
  </Layout>
}

Edit.getInitialProps = async (ctx: any) => {
  const userGroupService = new UserGroupService(ctx)
  const userService = new UserService(ctx)
  const query = ctx.query;
  let error: any;

  if (!query.id) {
    //error missing id
    error = {
      code: 9996,
      message: 'missing ID'
    }
  }

  let [userError, user] = await to(userService.detail({ id: query.id }));
  const [errorGroup, groups] = await to(userGroupService.index({ pageSize: -1 }));
  return {
    namespacesRequired: ['common', 'pages', 'menu'],
    error: error,
    user: user,
    groups: _.get(groups, "data", []),
  }
}
Edit.permissions = {
  "admin.users": "U"
};
export default Edit
