import React from 'react'
import { Radio, Button } from 'antd';
import _ from 'lodash'
const { Group } = Radio;

class FilterCheck extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            checked: undefined
        }
    }

    componentDidUpdate(prevProps) {
        if (JSON.stringify(prevProps.column.filteredValue) != JSON.stringify(this.props.column.filteredValue)) {
            this.setState({
                checked: undefined
            })
        }
    }

    onChange = (e) => {
        this.setState({
            checked: e.target.value
        }, () => {
                this.onSubmit()
        })
    }

    clear = () => {
        this.setState({checked: undefined}, () => {this.onSubmit()})
    }

    onSubmit = () => {
        const { column, confirm } = this.props
        let filters = {};
        if (this.state.checked) {
            filters = {
                field: column.field,
                operator: this.props.operator || '=',
                value: this.state.checked
            }
        }
        confirm(filters)
    }
    render() {
        const { confirm, column, ...otherProps } = this.props
        const {checked} = this.state
        return <div style={{ padding: 8 }}>
            <Group
                {...otherProps}
                ref={ref => this.refInput = ref}
                value={checked}
                onChange={this.onChange}
                options={[{ label: column.text[0], value: 0 },{ label: column.text[1], value: 1 }]}
                style={{ width: "256px", marginBottom: 8, }}
            />
            <Button onClick={this.clear} danger >Clear</Button>
        </div>
    }
}

export default FilterCheck
